import swal from "sweetalert"
import axios from "axios";
import { useState,useEffect }  from "react";
import { Link,useNavigate} from "react-router-dom";
const URI = "http://localhost:8080/cuenta/"

let headers = {
    "usuario" : sessionStorage.getItem("usuario"),
    "clave"   : sessionStorage.getItem("clave")
  };
const Cuenta = () => {
    const navigate = useNavigate();
    const [cuentas, setCuentas] = useState([])
    useEffect(() =>{
        getCuentas()
    })

    const getCuentas = async () =>{
        try {
            
            const res = await axios({
                method : "GET",
                url : URI + "consulta_cuenta",
                headers: headers 
               
            });
            
            setCuentas(res.data)
            
            //console.log(clientes)
        }
        catch (error) {
            
        }
    }


    return(
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <table className='table'>
                        <thead className='table-primary'>
                            <tr>
                                <th>Cuenta</th>
                                <th>Fecha Apertura</th>
                                <th>Saldo</th>
                            </tr>
                        </thead>
                        <tbody>
                            { clientes.map ( (cliente) => (
                                <tr key={ cliente.id_cliente}>
                                    <td> { cliente.id_cliente } </td>
                                    <td> { cliente.nombre_cliente } </td>
                                    <td> { cliente.clave_cliente } </td>
                                    <td>
                                        <Link to={`/editar/${cliente.id_cliente}`} className='btn btn-info'><i className="fas fa-edit"></i></Link>
                                        <button onClick={() => deleteCliente(cliente.id_cliente)} className='btn btn-danger'><i className="fas fa-trash-alt"></i></button>
                                    </td>
                                </tr>
                            )) }
                        </tbody>
                    </table>
                </div>    
            </div>
        </div>
    );
};

export default Cliente;
