import swal from "sweetalert"
import axios from "axios";
import { useState,useEffect }  from "react";
import { Link,useNavigate} from "react-router-dom";
const URI = "http://localhost:8080/cuenta/"

let headers = {
    "usuario" : sessionStorage.getItem("usuario"),
    "clave"   : sessionStorage.getItem("clave")
  };
const Cuenta = () => {
    //la asignacion useNavegate a la constante navigate puede ayudar a redirigir a otras rutas de la aplicacion
    const navigate = useNavigate();
    //useState[] agregar el estado a componentes funcionales, se declara la variable cuentas y la funcion setCuentas para actualizar el estado 
    const [cuentas, setCuentas] = useState([])

    useEffect(() =>{
        getCuentas()
    })

    const getCuentas = async () =>{
        //e.preventDefault();
        try {
            
            const res = await axios({
                method : "GET",
                url : URI + "consulta_cuenta?idc="+sessionStorage.getItem("usuario"),
                headers: headers 
               
            });
            
                setCuentas(res.data)

            //console.log(clientes)
        }
        catch (error) {
            swal("No tiene Acceso a esta Opción!", "Presiona el butón!", "error");
            navigate("/");
        }
    }

    return(
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <table className='table'>
                        <thead className='table-primary'>
                            <tr>
                                <th>Cuenta</th>
                                <th>Fecha Apertura</th>
                                <th>Saldo</th>
                                <th>Años de antiguedad</th>
                            </tr>
                        </thead>
                        <tbody>
                            
                            { cuentas.map ( (cuenta) => (
                               
                                <tr key={ cuenta.id_cuenta}>
                                    <td> { cuenta.id_cuenta } </td>
                                    <td> { cuenta.fecha_apertura.substring(0,10) } </td>
                                    <td> { cuenta.saldo_cuenta } </td>
                                    <td> { cuenta.antiguedad} </td>
                                    <td>
                                        <Link to={`/deposito/${cuenta.id_cuenta}`} className='btn btn-info mr-2'><i className="fas fa-donate"></i>Depósito</Link>
                                        <Link to={`/retiro/${cuenta.id_cuenta}`} className='btn btn-warning'><i className="fas fa-money-bill-alt"></i>Retiro</Link>
                                        <Link to={`/movimiento/${cuenta.id_cuenta}`} className='btn btn-success'><i className="fas fa-file-invoice-dollar"></i>Movimientos</Link>
                                        <Link to={`/crearprestamo/${cuenta.id_cuenta}`} className='btn btn-success'><i className="fas fa-file-invoice-dollar"></i>Crear prestamo</Link>
                                    </td>
                                </tr>
                            )) }
                           
                        </tbody>
                    </table>
                </div>    
            </div>
            <div className="row ">
                <div className="col-8"></div>
                <div className="col-3">
                    <p>¿Desea solicitar un prestamo?</p>
                    {/* { prestamos.map ( (prestamo) => (
                        <Link to={`/prestamo/${prestamo.id_prestamo}`} className='btn btn-info'><i className="fas fa-donate"></i>Solicitar Prestamo</Link> */}
                    <button className="btn btn-success"><a className="nav-link active" aria-current="page" href="/prestamo">Solicitar prestamo</a></button>
                    {/* )) } */}
                </div>
            </div>
        </div>
    );
};

export default Cuenta;
