import swal from "sweetalert";
import React, { useState } from 'react';
import { useHistory } from "react-router-dom";
import axios from 'axios';
const URI = "http://localhost:8080/cliente/"

const Registrarse = () => {
    const history = useHistory(); // Hook para acceder a la navegación
  
    const [usuario, setUsuario] = useState({
      nombre_cliente: "",
      clave_cliente: ""
    });
  
    const handleChange = (event) => {
      const { name, value } = event.target;
      setUsuario({ ...usuario, [name]: value });
    };
  
    const handleSubmit = (event) => {
      event.preventDefault();
      if (usuario.clave_cliente && usuario.nombre_cliente) {
        axios.post(`${URI}registrarse`, usuario)
          .then((response) => {
            swal("¡Registro exitoso!", "El usuario ha sido registrado correctamente.", "success");
            history.push("./Home"); // Redireccionar a la página que desees después del registro
          })
          .catch((error) => {
            swal("¡Error!", "Ha ocurrido un error al registrar el usuario.", "error");
          });
      } else {
        swal("¡Campos incompletos!", "Por favor, completa todos los campos.", "warning");
      }
    };
  
    return (
      <div>
        <h2>Registro de Usuario</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nombre:</label>
            <input type="text" name="nombre_cliente" onChange={handleChange} />
          </div>
          <div>
            <label>Clave:</label>
            <input type="password" name="clave_cliente" onChange={handleChange} />
          </div>
          <button type="submit">Registrar</button>
        </form>
      </div>
    );
  };
  
  export default Registrarse;
