import axios from "axios";
import { useState,useEffect }  from "react";
import { useNavigate, useParams} from "react-router-dom";
const URI = "http://localhost:8080/cuenta/"

let headers = {
    "usuario" : sessionStorage.getItem("usuario"),
    "clave"   : sessionStorage.getItem("clave")
  };
const Crear = () => {
    const [id_cuenta, setId_cuenta] = useState("");
    const [fecha_apertura, setFecha_apertura] = useState("");
    const [saldo_cuenta, setSaldo_cuenta] = useState("");
    const navigate = useNavigate();

    const {id} = useParams()

    const editar = async (e) => {
        e.preventDefault();
        // const UpdateCuenta= await axios({
        //     method: "PUT",
        //     url: URI + "deposito?idcta="+id_cuenta+"&valor_deposito="+valor_deposito,
        //     headers: headers 
        //   });
        
        const InsertCuenta= await axios({
            method: "POST",
            url: URI+"crear_cuenta?idcta="+id_cuenta+"&valor_deposito="+saldo_cuenta,
            headers: headers 
          });
          
        navigate("/cliente");
      };

      useEffect( ()=>{
        getCuentaById()
    },[])

    const getCuentaById = async () => {

        const res =  await axios({
            method: "GET",
            url: URI+"list/"+id,
            headers: headers 
          });
        //alert(URI+"list/"+id)
        setId_cuenta(res.data.id_cuenta)
        setFecha_apertura(res.data.fecha_apertura)
        setSaldo_cuenta(res.data.saldo_cuenta)
    }

    return(
        <div>
        <h3>Crear Cuenta</h3>
        <div className="container col-2">
        <form onSubmit={editar}>
            <div className="mb-3">
            <label className="form-label">ID</label>
            <textarea
                value={id_cuenta}
                onChange={(e) => setId_cuenta(e.target.value)}
                type="text"
                required
                onInvalid={e => e.target.setCustomValidity('El campo ID es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Fecha Apertura</label>
            <textarea
                value={fecha_apertura.substring(0,10)}
                onChange={(e) => setFecha_apertura(e.target.value)}
                type="text"
                required
                onInvalid={e => e.target.setCustomValidity('El campo Fecha apertura es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
                
                
            

            />
            </div>
            <div className="mb-3">
            <label className="form-label">Saldo Cuenta</label>
            <textarea
                value={saldo_cuenta}
                onChange={(e) => setSaldo_cuenta(e.target.value)}
                type="number"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Valor Dep贸sito</label>
            <textarea
                value={valor_deposito}
                onChange={(e) => setValor_deposito(e.target.value)}
                type="number"
                required
                onInvalid={e => e.target.setCustomValidity('El campo Valor Dep贸sito es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <button type="submit" className="btn btn-primary">
            Guardar
            </button>
        </form>
    </div>
    </div>
    );
};

export default Crear;
