import axios from "axios";
import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
const URI = "http://localhost:8000/prestamo"

let headers = {
  "usuario" : sessionStorage.getItem("usuario"),
  "clave"   : sessionStorage.getItem("clave")
};

const CrearPrestamo = () => {
  const navigate = useNavigate();
  
  const [saldoSolicitado, setSaldoSolicitado] = useState(0);
  const [cuotas, setCuotas] = useState(0);
  const [idCta, setIdCta] = useState("");
  
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "/crear_prestamo",
        {
          saldo_solicitado: saldoSolicitado,
          cuotas: cuotas,
          idcta: idCta,
        },
      );

      if (response.status === 200) {
        // Préstamo creado exitosamente, puedes hacer la navegación a la página deseada
        navigate("/cuenta");
      } else {
        // Manejar el error de acuerdo a tus necesidades
      }
    } catch (error) {
      // Manejar el error de acuerdo a tus necesidades
    }
  };

  return (
    <div className='container'>
      <h2>Crear Préstamo</h2>
      <form onSubmit={handleSubmit}>
      <div className="mb-3">
          <label className="form-label">Monto</label>
          <input
            type="number"
            placeholder="Saldo Solicitado"
            value={saldoSolicitado}
            onChange={(e) => setSaldoSolicitado(e.target.value)}
          />
      </div>
      <div className="mb-3">
      <label className="form-label">Número de cuotas</label>
        <input
          type="number"
          placeholder="Cuotas"
          value={cuotas}
          onChange={(e) => setCuotas(e.target.value)}
        />
      </div>
      <div className="mb-3">
      <label className="form-label">ID cuenta</label>
        <input
          type="text"
          placeholder="ID de Cuenta"
          value={idCta}
          onChange={(e) => setIdCta(e.target.value)}
        />
      </div>
        <button type="submit" className="btn btn-primary">Crear Préstamo</button>
      </form>
    </div>
  );
};

export default CrearPrestamo;
