import swal from "sweetalert";
import axios from "axios";
import { useState,useEffect }  from "react";
import { useNavigate, useParams} from "react-router-dom";
const URI = "http://localhost:8080/prestamo/"


//sessionStorage.setItem("usuario",
//sessionStorage.setItem("clave",123456);
let headers = {
    "usuario" : sessionStorage.getItem("usuario"),
    "clave"   : sessionStorage.getItem("clave")
  };

const MostrarPrestamo = () => {
   
    const [id_prestamo, setId_Prestamo] = useState([])
    const [id_cuenta, setId_cuenta] = useState("");
    const [fecha_solicitud, setFecha_solicitud] = useState("");
    const [saldo_solicitado, setSaldo_solicitado] = useState("");
    const [n_cuotas, setN_cuotas] = useState("");
    const [valor_cuota, setValor_cuota] = useState("");
    const navigate = useNavigate();

    
    const {id} = useParams()

    const editar = async (e) => {
        e.preventDefault();
        const UpdateCuenta= await axios({
            method: "PUT",
            url: URI + "deposito?idcta="+id_cuenta+"&valor_deposito="+valor_deposito,
            headers: headers 
          });
        
        const InsertTransaccion= await axios({
            method: "POST",
            url: URI1+"crear_transaccion?idcta="+id_cuenta+"&valor_transaccion="+valor_deposito+"&tipo=D",
            headers: headers 
          });
          
        navigate("/cliente");
      };

      useEffect( ()=>{
        getCuentaById()
    },[])

    const getPrestamos = async () =>{
        
        const res = await axios({
            method : "GET",
            url : URI + "list/"+id,
            headers: headers
           
        });
        
        setId_cuenta(res.data.setId_Prestamo)
        setFecha_apertura(res.data.fecha_apertura)
        setSaldo_cuenta(res.data.saldo_cuenta)    
            
        
    }
    useEffect(() =>{
        getPrestamos()
    })
     
    return(
        <div>
        <h3>Depósito</h3>
        <div className="container col-2">
        <form onSubmit={editar}>
            <div className="mb-3">
            <label className="form-label">ID</label>
            <textarea
                value={id_cuenta}
                onChange={(e) => setId_cuenta(e.target.value)}
                type="text"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Fecha Apertura</label>
            <textarea
                value={fecha_apertura.substring(0,10)}
                onChange={(e) => setFecha_apertura(e.target.value)}
                type="text"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Saldo Cuenta</label>
            <textarea
                value={saldo_cuenta}
                onChange={(e) => setSaldo_cuenta(e.target.value)}
                type="number"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Valor Depósito</label>
            <textarea
                value={valor_deposito}
                onChange={(e) => setValor_deposito(e.target.value)}
                type="number"
                required
                onInvalid={e => e.target.setCustomValidity('El campo Valor Depósito es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <button type="submit" className="btn btn-primary">
            Guardar
            </button>
        </form>
    </div>
    </div>
    );
};

export default Deposito_prestamo;
