import axios from "axios";
import { useState,useEffect }  from "react";
import { useNavigate, useParams} from "react-router-dom";
const URI = "http://localhost:8080/prestamo/"


const CrearPrestamo = () => {
    const [id_cuenta, setId_cuenta] = useState("");
    const [fecha_solicitud, setFecha_solicitud] = useState("");
    const [saldo_solicitado, setSaldo_solicitado] = useState("");
    const [n_cuotas, setN_cuotas] = useState("");
    const [valor_cuota, setValor_cuota] = useState("");
    const navigate = useNavigate();

    const {id} = useParams()

    const editar = async (e) => {
        e.preventDefault();
        // const InsertTransanccionPrestamo= await axios({
        //     method: "POST",
        //     url: URI + "deposito?idcta="+id_cuenta+"&valor_deposito="+valor_deposito,
        //     headers: headers 
        //   });
        let headers = {
            "usuario" : sessionStorage.getItem("usuario"),
            "clave"   : sessionStorage.getItem("clave")
          };

        const InsertNewPrestamo= await axios.post(`${URI}crear_prestamo`, {
            idcta: id_cuenta,
            saldo_solicitado: saldo_solicitado,
            cuotas: n_cuotas
        }, { headers });

        navigate("/cliente");
      };

      useEffect( ()=>{
        getPrestamoById()
    },[])

    const getPrestamoById = async () => {
        const headers = {
            "usuario": sessionStorage.getItem("usuario"),
            "clave": sessionStorage.getItem("clave"),
        };

         const res = await axios.get(`${URI}list/${id}`, { headers });
        //alert(URI+"list/"+id)
        setId_cuenta(res.data.id_cuenta);
        setFecha_solicitud(res.data.fecha_solicitud);
        setValor_cuota(res.data.valor_cuota);
    }

    return(
        <div>
        <h3>Solicitud de prestamo</h3>
        <div className="container col-2">
        <form onSubmit={editar}>
            <div className="mb-3">
            <label className="form-label">ID</label>
            <input
                value={id_cuenta}
                onChange={(e) => setId_cuenta(e.target.value)}
                type="text"
                disabled
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Fecha solicitud</label>
            <input
                value={fecha_solicitud.substring(0,10)}
                onChange={(e) => setFecha_solicitud(e.target.value)}
                type="text"
                disabled
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Saldo solicitado</label>
            <input
                value={saldo_solicitado}
                onChange={(e) => setSaldo_solicitado(e.target.value)}
                type="number"
                required
                onInvalid={e => e.target.setCustomValidity('El campo Saldo solicitado es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Cantidad de cuotas</label>
            <input
                value={n_cuotas}
                onChange={(e) => setN_cuotas(e.target.value)}
                type="number"
                required
                onInvalid={e => e.target.setCustomValidity('El campo Numero de cuotas es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Valor cuota</label>
            <input
                value={valor_cuota}
                onChange={(e) => setValor_cuota(e.target.value)}
                type="number"
                disabled="false"
                className="form-control"
            />
            </div>
            <button type="submit" className="btn btn-primary" yyyy>
            Guardar
            </button>
        </form>
    </div>
    </div>
    );
};

export default CrearPrestamo;
