// import React, { useState } from 'react';
// import axios from 'axios';
// import { useHistory } from 'react-router-dom';
import axios from "axios";
import { useState,useEffect }  from "react";
import { useNavigate, useParams} from "react-router-dom";

const URI = "http://localhost:8080/cuenta/";

const CrearCuenta = () => {
  const [idCuenta, setIdCuenta] = useState('');
  const [fechaApertura, setFechaApertura] = useState('');
  const [saldoCuenta, setSaldoCuenta] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validar y procesar los datos del formulario
    if (!idCuenta || !fechaApertura || !saldoCuenta) {
      alert('Por favor, completa todos los campos');
      return;
    }

    try {
      // Crear el objeto con los datos de la nueva cuenta
      const nuevaCuenta = {
        id_cuenta: idCuenta,
        fecha_apertura: fechaApertura,
        saldo_cuenta: parseFloat(saldoCuenta),
      };

      // Realizar la llamada a la API para crear la nueva cuenta
      await axios.post(URI, nuevaCuenta);

      // Redirigir a la página de cuentas después de guardar la cuenta
      history.push('/cuentas');
    } catch (error) {
      alert('Error al guardar la cuenta. Inténtalo de nuevo.');
    }
  };

  return (
    <div className='container'>
      <div className='row'>
        <div className='col'>
          <h3>Crear Nueva Cuenta</h3>
          <form onSubmit={handleSubmit}>
            <div className='mb-3'>
              <label className='form-label'>ID Cuenta:</label>
              <input
                type='text'
                value={idCuenta}
                onChange={(e) => setIdCuenta(e.target.value)}
                className='form-control'
              />
            </div>
            <div className='mb-3'>
              <label className='form-label'>Fecha Apertura:</label>
              <input
                type='date'
                value={fechaApertura}
                onChange={(e) => setFechaApertura(e.target.value)}
                className='form-control'
              />
            </div>
            <div className='mb-3'>
              <label className='form-label'>Saldo Cuenta:</label>
              <input
                type='number'
                value={saldoCuenta}
                onChange={(e) => setSaldoCuenta(e.target.value)}
                className='form-control'
              />
            </div>
            <button type='submit' className='btn btn-primary'>
              Guardar
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CrearCuenta;
