    const navigate = useNavigate();
    const [clientes, setClientes] = useState([])
    const [id_cliente, setId_cliente] = useState("");
    const [clave_cliente, setClave_cliente] = useState("");
    //useEffect(() =>{
    //    getClientes()
    //})

    const guardar = async () => {
        //alert(id_cliente);
        //alert(clave_cliente);
        try {          
            const res = await axios({
                method : "GET",
                url: URI + "login?usuario="+id_cliente+"&clave="+clave_cliente
               
            });
            //alert(URI + "login?usuario="+id_cliente+"&clave="+clave_cliente);
            setClientes(res.data)
            if (res.data.id_cliente!=null) {
                alert("No Autorizado")
            } else {
                
            }
    
            //sessionStorage.setItem("usuario",id_cliente);
            //sessionStorage.setItem("clave",clave_cliente);
            //console.log(clientes)
        }
        catch (error) {
            alert("No se ejecuto el GET")
        }

        /*
        e.preventDefault();
    
        const res = await axios({
            method: "GET",
            url: URI + "login?usuario="+id_cliente+"&clave="+clave_cliente
          });
          setClientes(res.data)

          if (clientes!=null){
            sessionStorage.setItem("usuario",id_cliente);
            sessionStorage.setItem("clave",clave_cliente);
            navigate("/clientes");  
        }
        else{
            alert("Usuario No Autorizado")
        }
          //console.log(cliente
        //navigate("/clientes");
        */
      };

    return(
        <div>
            <h3>Login</h3>
            <form onSubmit={guardar}>
                <div className="mb-3">
                    <label className="form-label">ID</label>
                    <input
                        value={id_cliente}
                        onChange={(e) => setId_cliente(e.target.value)}
                        type="text"
                        className="form-control"
                />
                </div>
                <div className="mb-3">
                    <label className="form-label">Clave</label>
                    <input
                        value={clave_cliente}
                        onChange={(e) => setClave_cliente(e.target.value)}
                        type="password"
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn btn-primary">
                Login
                </button>
            </form>
        </div>
    );
};

export default Logearse;
