import swal from "sweetalert"
import axios from "axios";
import { useState,useEffect }  from "react";
import { useNavigate} from "react-router-dom";
const URI = "http://localhost:8080/cliente/"

const Logearse = () => {
    const navigate = useNavigate();
    const [clientes, setClientes] = useState([])
    const [id_cliente, setId_cliente] = useState("");
    const [clave_cliente, setClave_cliente] = useState("");
    useEffect(() =>{
        getClientes()
    })

    const getClientes = async () => {

        try {
            
            const res = await axios({
                method : "GET",
                url: URI + "login?usuario="+id_cliente+"&clave="+clave_cliente
               
            });
            alert(URI + "login?usuario="+clientes.id_cliente+"&clave="+clientes.clave_cliente);
            setClientes(res.data)
            sessionStorage.setItem("usuario",clientes.id_cliente);
            sessionStorage.setItem("clave",clientes.clave_cliente);
            //console.log(clientes)
        }
        catch (error) {
            
        }

        /*
        e.preventDefault();
    
        const res = await axios({
            method: "GET",
            url: URI + "login?usuario="+id_cliente+"&clave="+clave_cliente
          });
          setClientes(res.data)

          if (clientes!=null){
            sessionStorage.setItem("usuario",id_cliente);
            sessionStorage.setItem("clave",clave_cliente);
            navigate("/clientes");  
        }
        else{
            alert("Usuario No Autorizado")
        }
          //console.log(cliente
        //navigate("/clientes");
        */
      };

    return(
        <div>
            <h3>Login</h3>
            <form onSubmit={getClientes}>
                <div className="mb-3">
                    <label className="form-label">ID</label>
                    <input
                        value={id_cliente}
                        onChange={(e) => setId_cliente(e.target.value)}
                        type="text"
                        className="form-control"
                />
                </div>
                <div className="mb-3">
                    <label className="form-label">Clave</label>
                    <input
                        value={clave_cliente}
                        onChange={(e) => setClave_cliente(e.target.value)}
                        type="password"
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn btn-primary">
                Login
                </button>
            </form>
        </div>
    );
};

export default Logearse;
