import swal from "sweetalert";
import axios from "axios";
import { useState,useEffect }  from "react";
import { useNavigate, useParams} from "react-router-dom";
const URI = "http://localhost:8080/prestamo/"

let headers = {
    "usuario" : sessionStorage.getItem("usuario"),
    "clave"   : sessionStorage.getItem("clave")
  };

  const Prestamo = () => {
    const [id_prestamo, setId_prestamo] = useState("");
    const [fecha_solicitud, setFecha_solicitud] = useState("");
    const [saldo_solicitado, setSaldo_solicitado] = useState("");
    const [n_cuotas, setN_cuotas] = useState("");
    const [valor_cuota, setValor_cuota] = useState("");
    // const [valor_deposito_c, setValor_deposito_c] = useState("");
    // const [saldo_pendiente, setSaldo_pendiente] = useState("");
    const navigate = useNavigate();

    const {id} = useParams()

    const crear = async (e) => {
        e.preventDefault();
        // const UpdatePrestamo= await axios({
        //     method: "PUT",
        //     url: URI + "deposito_cuota?idp="+id_prestamo+"&valor_deposito_c="+valor_deposito_c,
        //     headers: headers 
        //   });
        
        const InsertPrestamo= await axios({
            method: "POST",
            url: URI+"crear_prestamo",
            headers: headers 
          });
          
        navigate("/cliente");
      };

      useEffect( ()=>{
        getPrestamoById()
    },[])

    const getPrestamoById = async () => {

        const res =  await axios({
            method: "GET",
            url: URI+"list/"+id,
            headers: headers 
          });
        //alert(URI+"list/"+id)
        setId_prestamo(res.data.id_prestamo)
        setFecha_solicitud(res.data.fecha_solicitud)
        setSaldo_solicitado(res.data.saldo_solicitado)
    }

    return(
        <div>
        <h3>DepÃ³sito</h3>
        <div className="container col-2">
        <form onSubmit={crear}>
            <div className="mb-3">
            <label className="form-label">ID</label>
            <textarea
                value={id_prestamo}
                onChange={(e) => setId_prestamo(e.target.value)}
                type="text"
                required
                onInvalid={e => e.target.setCustomValidity('El campo ID es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">fecha solicitud</label>
            <textarea
                value={fecha_solicitud.substring(0,10)}
                onChange={(e) => setFecha_solicitud(e.target.value)}
                type="text"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">saldo solicitado</label>
            <textarea
                value={saldo_solicitado}
                onChange={(e) => setSaldo_solicitado(e.target.value)}
                type="number"
                required
                onInvalid={e => e.target.setCustomValidity('El campo Saldo solicitado es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Numero de cuotas</label>
            <textarea
                value={n_cuotas}
                onChange={(e) => setN_cuotas(e.target.value)}
                type="number"
                required
                onInvalid={e => e.target.setCustomValidity('El campo numero de cuotas es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Valor por cuota</label>
            <textarea
                value={valor_cuota}
                onChange={(e) => setValor_cuota(e.target.value)}
                type="number"
                disabled="false"
                className="form-control"
            />
            </div>
            <button type="submit" className="btn btn-primary">
            Guardar
            </button>
        </form>
    </div>
    </div>
    );
};

export default Prestamo;
