import swal from "sweetalert"
import axios from "axios";
import { useState }  from "react";
import { useNavigate } from "react-router-dom";
const URI = "http://localhost:8080/cliente/"

const Logearse = () => {
    const navigate = useNavigate();
    const [clientes, setClientes] = useState([])
    const [id_cliente, setId_cliente] = useState("");
    const [clave_cliente, setClave_cliente] = useState("");
   

    const guardar = async () => {
        //alert(id_cliente)
        try {          
            const res = await axios({
                method : "GET",
                url: URI + "login?usuario="+id_cliente+"&clave="+clave_cliente
            });

            setClientes(res.data)
            if (res.data.id_cliente==null) {
                //swal("Usuario NO Autorizado");
                //alert("NO Autorizado")
                swal("Cliente NO Autorizado",{ 
                    icon: "error",
                });
                //guardar()
                
            } else {
               sessionStorage.setItem("usuario",id_cliente);
               sessionStorage.setItem("clave",clave_cliente);
            }
            //navigate("/clientes");
            
        }
        catch (error) {
            swal("Operación NO realizada")
        }

      };

    return(
        <div>
            <h3>Login</h3>
            <form onSubmit={guardar}>
                <div className="mb-3">
                    <label className="form-label">ID</label>
                    <input
                        value={id_cliente}
                        onChange={(e) => setId_cliente(e.target.value)}
                        type="text"
                        required
                        className="form-control"
                />
                </div>
                <div className="mb-3">
                    <label className="form-label">Clave</label>
                    <input
                        value={clave_cliente}
                        onChange={(e) => setClave_cliente(e.target.value)}
                        type="password"
                        required
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn btn-primary">
                Login
                </button>
            </form>
        </div>
    );
};

export default Logearse;
