import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CrearPrestamo = () => {
  const navigate = useNavigate();
  
  const [clave, setClave] = useState("");
  const [usuario, setUsuario] = useState("");
  const [saldoSolicitado, setSaldoSolicitado] = useState(0);
  const [cuotas, setCuotas] = useState(0);
  const [idCta, setIdCta] = useState("");
  
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "/crear_prestamo",
        {
          saldo_solicitado: saldoSolicitado,
          cuotas: cuotas,
          idcta: idCta,
        },
        {
          headers: {
            clave: clave,
            usuario: usuario,
          },
        }
      );

      if (response.status === 200) {
        // Préstamo creado exitosamente, puedes hacer la navegación a la página deseada
        navigate("/c");
      } else {
        // Manejar el error de acuerdo a tus necesidades
      }
    } catch (error) {
      // Manejar el error de acuerdo a tus necesidades
    }
  };

  return (
    <div>
      <h2>Crear Préstamo</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Clave"
          value={clave}
          onChange={(e) => setClave(e.target.value)}
        />
        <input
          type="text"
          placeholder="Usuario"
          value={usuario}
          onChange={(e) => setUsuario(e.target.value)}
        />
        <input
          type="number"
          placeholder="Saldo Solicitado"
          value={saldoSolicitado}
          onChange={(e) => setSaldoSolicitado(e.target.value)}
        />
        <input
          type="number"
          placeholder="Cuotas"
          value={cuotas}
          onChange={(e) => setCuotas(e.target.value)}
        />
        <input
          type="text"
          placeholder="ID de Cuenta"
          value={idCta}
          onChange={(e) => setIdCta(e.target.value)}
        />
        <button type="submit">Crear Préstamo</button>
      </form>
    </div>
  );
};

export default CrearPrestamo;
