import swal from "sweetalert";
import axios from "axios";
import { useState,useEffect }  from "react";
import { useNavigate, useParams} from "react-router-dom";
const URI = "http://localhost:8080/prestamo/"


//sessionStorage.setItem("usuario",
//sessionStorage.setItem("clave",123456);
let headers = {
    "usuario" : sessionStorage.getItem("usuario"),
    "clave"   : sessionStorage.getItem("clave")
  };

const MostrarPrestamo = () => {
   
    const [id_prestamo, setId_Prestamo] = useState([])
    const [id_cuenta, setId_cuenta] = useState("");
    const [fecha_solicitud, setFecha_solicitud] = useState("");
    const [saldo_solicitado, setSaldo_solicitado] = useState("");
    const [n_cuotas, setN_cuotas] = useState("");
    const [valor_cuota, setValor_cuota] = useState("");
    const [valor_deposito_c, setValor_deposito_c] = useState("");
    const navigate = useNavigate();

    
    const {id} = useParams()

    const editar = async (e) => {
        e.preventDefault();
        const UpdatePrestamo= await axios({
            method: "PUT",
            url: URI + "deposito_cuota?idp="+id_prestamo+"&valor_deposito_c="+valor_deposito_c,
            headers: headers 
           
          });
          
        navigate("/cliente");
      };

      useEffect( ()=>{
        getPrestamos()
    },[])

    const getPrestamos = async () =>{
        
        const res = await axios({
            method : "GET",
            url : URI + "list/"+id,
            headers: headers
           
        });
        
        setId_prestamo(res.data.setId_Prestamo)
        setSaldo_solicitado(res.data.saldo_solicitado)
        setN_cuotas(res.data.n_cuotas)
        setValor_cuota(res.data.valor_cuota)    
            
        
    }
     
    return(
        <div>
        <h3>Depósito cuota</h3>
        <div className="container col-2">
        <form onSubmit={editar}>
            <div className="mb-3">
            <label className="form-label">ID</label>
            <textarea
                value={id_prestamo}
                onChange={(e) => setId_cuenta(e.target.value)}
                type="text"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Saldo solicitado</label>
            <textarea
                value={fecha_apertura.substring(0,10)}
                onChange={(e) => setSaldo_solicitado(e.target.value)}
                type="text"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Numero de cuotas</label>
            <textarea
                value={n_cuotas}
                onChange={(e) => setSaldo_cuenta(e.target.value)}
                type="number"
                disabled="false"
                className="form-control"
            />
            </div>
            <div className="mb-3">
            <label className="form-label">Valor Depósito</label>
            <textarea
                value={valor_deposito}
                onChange={(e) => setValor_deposito(e.target.value)}
                type="number"
                required
                onInvalid={e => e.target.setCustomValidity('El campo Valor Depósito es obligatorio')}
                onInput={e => e.target.setCustomValidity('')}
                className="form-control"
            />
            </div>
            <button type="submit" className="btn btn-primary">
            Guardar
            </button>
        </form>
    </div>
    </div>
    );
};

export default Deposito_prestamo;
