import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CrearPrestamo = () => {
  const [saldoSolicitado, setSaldoSolicitado] = useState("");
  const [nCuotas, setNCuotas] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const usuario = sessionStorage.getItem("usuario");
      const clave = sessionStorage.getItem("clave");
      const headers = {
        usuario: usuario,
        clave: clave,
      };

      const response = await axios.post(
        "http://localhost:8080/prestamo/",
        {
          saldo_solicitado: saldoSolicitado,
          n_cuotas: nCuotas,
        },
        { headers }
      );

      if (response.status === 200) {
        // Redireccionar a la página deseada después de guardar el préstamo
        navigate("/cliente");
      } else {
        // Mostrar algún mensaje de error en caso necesario
        console.log("Error al crear el préstamo");
      }
    } catch (error) {
      // Manejar el error, por ejemplo, mostrar un mensaje de error en la consola
      console.error("Error al crear el préstamo:", error);
    }
  };

  return (
    <div>
      <h3>Solicitud de préstamo</h3>
      <div className="container col-6">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Saldo solicitado</label>
            <input
              value={saldoSolicitado}
              onChange={(e) => setSaldoSolicitado(e.target.value)}
              type="number"
              required
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Cantidad de cuotas</label>
            <input
              value={nCuotas}
              onChange={(e) => setNCuotas(e.target.value)}
              type="number"
              required
              className="form-control"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Guardar
          </button>
        </form>
      </div>
    </div>
  );
};

export default CrearPrestamo;

