import swal from "sweetalert"
import axios from "axios";
import { useState,useEffect }  from "react";
import { Link,useNavigate} from "react-router-dom";
const URI = "http://localhost:8080/cliente/"

sessionStorage.setItem("usuario",1);
sessionStorage.setItem("clave",123456);
let headers = {
    usuario:sessionStorage.getItem("usuario"),
    clave:sessionStorage.getItem("usuario"),
  };

const MostrarCliente = () => {
    const navigate = useNavigate();
    const [clientes, setClientes] = useState([])
    useEffect(() =>{
        getClientes()
    })

    const getClientes = async () =>{
        try {
            
            const res = await axios({
                method : "GET",
                url : URI + "list",
                headers: headers 
                //url : URI +"login?usuario=1&clave=123456"
            });
            setClientes(res.data)
            //console.log(clientes)
        }
        catch (error) {
            
        }
    }

const deleteCliente = async (id) => {
    swal(
        {
            title: "Eliminar Registro",
            text: "Está seguro de eliminar registro?",
            icons: "Warning",
            buttons: true,
            dangerMode: true,
        })
        .then(async (willDelete) =>{
            if (willDelete){
                const res = await axios({
                    method: "DELETE",
                    url: URI + id
                });
                swal("El resgistro se eliminó satisfactoriamente",{ 
                    icon: "success",
                });
                getClientes()
            } else{
                swal("El registro no se borró")
            }
        });

}
     
return(
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <Link to="/crear" className='btn btn-primary mt-2 mb-2'><i className="fas fa-plus"></i></Link>
                    <table className='table'>
                        <thead className='table-primary'>
                            <tr>
                                <th>Id</th>
                                <th>Nombre</th>
                                <th>Clave</th>
                            </tr>
                        </thead>
                        <tbody>
                            { clientes.map ( (cliente) => (
                                <tr key={ cliente.id_cliente}>
                                    <td> { cliente.nombre_cliente } </td>
                                    <td> { cliente.clave_cliente } </td>
                                    <td>
                                        <Link to={`/editar/${cliente.id_cliente}`} className='btn btn-info'><i className="fas fa-edit"></i></Link>
                                        <button onClick={() => deleteCliente(cliente.id_cliente)} className='btn btn-danger'><i className="fas fa-trash-alt"></i></button>
                                    </td>
                                </tr>
                            )) }
                        </tbody>
                    </table>
                </div>    
            </div>
        </div>
    );
};

export default MostrarCliente;
